INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (9, '微语篇', 'bed', 'Joe works all the day. He goes home. He is tired, so he sits on the bed. He falls asleep. He wakes up in 6 hours. He has to work again.', '乔工作了一整天。他回家了。他累了，所以他坐在床上。他睡着了。他在六个小时后醒来了。他不得不再次工作了。', 0, 'f', '精通4B Unit 1 Lesson 1', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.537206+08', 2, 24, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (10, '微语篇', 'desk', 'We have just got my son a new computer as a Christmas gift. We need a desk to put the computer on. We then went to a furniture store. My son chose a black desk. It has four drawers. It goes well with his computer.', '我们刚给儿子买了一台新电脑作为圣诞礼物。我们还需要一张桌子放电脑。于是我们又去了家具店。儿子选了一张黑色的桌子。它有四个抽屉。它和电脑很配。', 0, 'f', '精通4B Unit 1 Lesson 1', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.543952+08', 2, 28, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (11, '微语篇', 'chair', 'My husband and I went to a furniture store last Saturday. We wanted to buy some chairs to go with our new table. My husband wanted to get some metal chairs. They are strong. I wanted to get some wooden chairs. They look classic. Finally we got four wooden chairs.', '上周六，我和丈夫去了一个家具店。我们想买几把椅子配新买的桌子。我丈夫想买金属椅子，他说结实。我想买木制椅子，因为看起来古典。最后我们买了四把木制椅子。', 0, 'f', '精通4B Unit 1 Lesson 1', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.550453+08', 2, 25, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (12, '微语篇', 'TV', '', '', 0, 'f', '精通4B Unit 1 Lesson 1', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.557184+08', 2, NULL, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (13, '微语篇', 'ball', 'The earth is a ball. It is round. There is a lot of land on the earth. There is also a lot of water on the earth. The earth is where we live.', '地球是一个球体。它是圆的。地球上有很多土地。地球上也有很多水。地球是我们生活的地方。', 0, 'f', '精通4B Unit 1 Lesson 1', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.563751+08', 2, 23, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (14, '微语篇', 'kite', 'The weather is sunny and windy. It is cool outside. It''s a good day to fly a kite. I have a red kite. Do you want to go kite flying with me? We can go to the park. There is lots of space in the park.', '天气晴朗，有风。外面很凉爽。这是一个放风筝的好日子。我有一个红色的风筝。你想和我一起去放风筝吗？我们可以去公园。公园里空间很大。', 0, 'f', '精通4B Unit 1 Lesson 1', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.570388+08', 2, 31, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (15, '微语篇', 'kitchen', 'Every day, our mother cooks dinner for us. She cooks in the kitchen. There are many tools in the kitchen. She cuts vegetables into small pieces with a knife. She cooks the meat in a pot and makes soup in another pot.', '妈妈每天为我们做晚饭。她在厨房做饭。厨房里有很多工具。她用刀把蔬菜切成小块。她在一个锅里炖肉，在另一个锅里做汤。', 0, 'f', '精通4B Unit 1 Lesson 1', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.577213+08', 2, 30, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (16, '微语篇', 'table', 'The walls of the room are white. The only thing in the room is a table. The table is red. The table has four legs. The table is a square. There is nothing on the table.', '房间的墙壁是白色的。房间里唯一的东西是一张桌子。桌子是红色的。它有四条腿，是正方形的。桌子上什么也没有。', 0, 'f', '精通4B Unit 1 Lesson 3', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.58397+08', 2, 34, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (17, '微语篇', 'study', 'When our father comes home from work, he wants to relax. He eats dinner with us. Then he goes to read books in the study. He likes to read history books. We have to be quiet when he is reading.', '爸爸下班回家后，想放松一下。他和我们一起吃晚饭。然后他去书房看书。他喜欢读历史书。当他读书时，我们必须安静。', 0, 'f', '精通4B Unit 1 Lesson 4', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.590251+08', 2, 33, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (18, '微语篇', 'computer', 'In my room, there is a bed. I sleep on the bed. There is also a table. There is a computer on the table. There is a chair in front of the table. I sit in the chair.', '在我的房间里有一张床。我在床上睡觉。还有一张桌子。桌子上有一台电脑。桌子前面有一把椅子。我在椅子上坐着。', 0, 'f', '精通4B Unit 1 Lesson 4', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.596674+08', 2, 27, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (19, '微语篇', 'picture', 'This picture was made by Picasso. Picasso was Spanish. He was one of the great artists in the world. He made more than 20,000 pictures in his life time. This picture is very colourful. It is also very famous.', '这幅画是毕加索的作品。毕加索是西班牙人。他是世界上伟大的艺术家之一。他一生画了两万多张画。这幅画色彩很丰富，也很有名。', 0, 'f', '精通4B Unit 1 Lesson 4', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.603117+08', 2, 32, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (20, '微语篇', 'clock', 'This is a clock. The clock stands in the corner of the street. It is an old style clock. It still works well. It always tells the correct time. Listen, the clock is ringing. It also attracts travelers.', '这是一只钟。它立在街的一角。这是一只老式钟。它依然很准，总能准确报时。听，钟正在响。它还吸引了不少游客。', 0, 'f', '精通4B Unit 1 Lesson 5', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.609609+08', 2, 26, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (21, '微语篇', 'China', 'Jane is a Canadian girl. She is a student. She will go to China during her summer vacation. She will visit some beautiful places in China. She will go shopping. She will try Chinese food. What''s more, she will learn Chinese. She will have a lot of fun in China.', '简是一位加拿大姑娘。她是个学生。今年暑假她要去中国。她要参观中国许多美丽的地方，她要购物，她要品尝中国食物。更重要的是，她要学中文。她在中国的旅游一定会很愉快。', 0, 'f', 'PEP3B Unit 1 Lesson 2', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.615857+08', 2, 73, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (22, '微语篇', 'she', 'Amy is a Chinese girl. She is a good student. She is good at math, and she likes reading, drawing, and computers. She goes to school at 8:00 in the morning and comes home at 5:30 in the afternoon. In the evening, she does her homework.', '艾米是一个中国女孩。她是一个好学生。她数学很好，她喜欢看书，画画，玩电脑。她每天早上8:00去上学，下午5:30回家。在晚上，她会做家庭作业。', 0, 'f', 'PEP3B Unit 1 Lesson 3', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.622289+08', 2, 75, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (23, '微语篇', 'pupil', 'I live in Canada. I go to elementary school. There are about 1,500 pupils in my school. There are 8 grades. There are 50 classes. There are about 30 pupils in each class. There are also many teachers in my school.', '我住在加拿大。我上小学。我校大约有1500名小学生，有8个年级，50个班。每班大约有30个学生。我校还有很多老师。', 0, 'f', 'PEP3B Unit 1 Lesson 3', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.629694+08', 2, 74, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (25, '微语篇', 'teacher', 'My mother is a teacher. She teaches English. She teaches in a middle school. She has many students. She likes her students. Her students bring her joy.', '我的妈妈是一位老师。她教英语。她在一所中学教书。她有许多学生。她喜欢她的学生们。她的学生们给她带来了快乐。', 0, 'f', 'PEP3B Unit 1 Lesson 3', '2019-01-23 16:49:21.8375+08', '2019-02-20 10:00:04.643366+08', 2, 77, NULL, NULL);
