INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (200, '微语篇', 'cold', 'It is very cold in winter. Sometimes, it snows. People wear warm clothes. People wear coats, hats, and boots. People like to stay inside because it is warm inside.', '冬天很冷。有时会下雪。人们穿着暖和的衣服。人们穿着大衣、戴着帽子，并穿着靴子。人们喜欢待在室内，因为室内温暖。', 0, 'f', 'PEP4B Unit 3 Lesson2', '2019-01-28 15:00:12.954+08', '2019-02-20 10:00:05.835036+08', 2, 557, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (201, '微语篇', 'warm', 'It''s the first day of summer. It''s a warm day today. It is a little bit windy. The sky is blue. There are no clouds in the sky. The sun is bright.', '今天是入夏第一天。今天天气很暖和。微风徐徐。天空很蓝，没有一片云彩。阳光灿烂。', 0, 'f', 'PEP4B Unit 3 Lesson2', '2019-01-28 15:00:14.682+08', '2019-02-20 10:00:05.841688+08', 2, 558, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (202, '微语篇', 'hot', 'Now it is summer. It is very hot today. A lot of people go swimming. Some people stay indoor with the air conditioning. In such hot weather, we should drink more water, stay in the shade or have more showers.', '现在是夏天。今天很热。很多人去游泳，有些人则待在有空调的室内。在这样的热天里，我们应该多喝水，待在阴凉处，或者多冲凉。', 0, 'f', 'PEP4B Unit 3 Lesson2', '2019-01-28 15:00:15.533+08', '2019-02-20 10:00:05.8484+08', 2, 559, NULL, NULL);
