INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (205, '微语篇', 'cloudy', 'Today, I like the weather. It is not raining. It is not sunny either. The weather is cloudy. It is not cold. It is not hot. It feels good.', '我喜欢今天的天气。今天没下雨，也不是阳光明媚。今天多云。不冷也不热，感觉很好。', 0, 'f', 'PEP4B Unit 3 Lesson3', '2019-01-28 15:00:17.509+08', '2019-02-20 10:00:05.869457+08', 2, 560, NULL, NULL);
