INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (216, '微语篇', 'spring', 'Winter is over. It''s spring time already. The weather is getting warmer. There are lots of beautiful flowers. The trees and grass are green. Summer is after spring.', '冬天结束了。已经是春天了。天气渐渐变暖。有很多漂亮的鲜花。树和草是绿色的。春天过后就是夏天。', 0, 'f', 'PEP5B Unit 2 Lesson2', '2019-01-28 15:00:28.449+08', '2019-02-20 10:00:05.945361+08', 2, 762, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (217, '微语篇', 'summer', 'In summer, it is hot and sunny. The days are long and the nights are short. It is the perfect time to go on vacation. You can go to the beach to swim and cool off. You can also go for a walk in the forest.', '夏天，天气炎热，阳光明媚。白天很长，夜晚很短。这是度假的最佳时间。你可以去海滩游泳，凉快凉快。你也可以到森林里散散步。', 0, 'f', 'PEP5B Unit 2 Lesson2', '2019-01-28 15:00:29.315+08', '2019-02-20 10:00:05.952316+08', 2, 763, NULL, NULL);
INSERT INTO "contents_article"("id", "title", "en_title", "content", "en_content", "order", "is_active", "description", "create_time", "update_time", "classify", "audio_id", "image_id", "video_id") VALUES (218, '微语篇', 'autumn', 'In autumn, leaves turn yellow and fall off. In winter, there are no leaves on the trees. In spring, the leaves grow back. In summer, there are many leaves.', '秋天，树叶变黄落下。冬天，树上没有树叶。春天，树叶重生。夏天，有许多树叶。', 0, 'f', 'PEP5B Unit 2 Lesson2', '2019-01-28 15:00:29.904+08', '2019-02-20 10:00:05.959398+08', 2, 764, NULL, NULL);
