INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (1, '{"text": "通过视听活动，使学生对本课要学的重点句型-Where are you from? - I’m from ... 产生兴趣"}', '{"text": "欣赏视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。同时，试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (2, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看，认真听，根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (3, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，每句之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句义，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (4, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型-Where are you from? - I’m from ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第2页，朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (5, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 3个人一组\n2. 从课文的两组角色（第一组：John, Amy, Wu Yifan；第二组：Miss White, Sarah, Chen Jie/Mike）中各选1个角色\n3. 仿照课文图，进行角色对话\n4. 完成后，3人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动\n3. 表演结束后，我会请你们上台来展示"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (7, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看，认真听，根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (8, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，每句之间给学生留出足够的跟读时间\n2. 学生借助图片不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (9, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型- I’m from ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第4页，朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (10, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 4个人一组；\n2. 从课文角色中选1个角色\n3. 仿照课文图，进行角色对话\n4. 完成后，4人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动\n3. 表演结束后，我会请你们上台来展示"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (11, '{"text": "识记词汇UK, Canada, USA, China，并了解其在语境中的应用"}', '{"text": "1. 看图片理解词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习4个单词。根据图片猜一猜它们分别是哪个国家的英文名称！注意看单词、认真听录音，并小声跟读。"}', '{"text": "依次呈现单词卡片，并且根据学生的反应，进行必要的重复呈现"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (12, '{"text": "通过人际活动巩固学习Let’s talk部分的核心句型：I’m ... I’m from ..."}', '{"text": "1. 看图中的国旗，说出所代表国家的国名。重复数次，直至熟练。\n2. 读出对话框里的句子。重复数次，直至熟练。\n3. 三个人一组，两人分别扮演Mike, Sarah，第三个是自己。\n4. Mike, Sarah分别介绍自己。然后其中一个人说What about you?, 第三个人用句型I’m ... I’m from … 说自己的姓名及国籍。\n5. 交换角色，继续活动。"}', '{"text": "1. 请把课本翻到第4页，看下面的活动\n2. 同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 将全班同学进行分组，每组三个人\n3. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (13, '{"text": "学生通过对歌谣的理解和朗读操练巩固四个核心词汇：Canada, UK, China, USA"}', '{"text": "1. 聆听录音\n2. 看着文字，跟读\n3. 朗读韵文\n4. 重复1-3步，直至朗朗上口"}', '{"text": "我们放松一下，学一首英文歌谣。大家要认真听、认真唱！看看谁唱得最好！"}', '{"text": "1. 按照活动步骤带领学生学唱歌谣\n2. 巡视并监督学生认真学唱"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (14, '{"text": "在歌曲中复现重点句型-Where are you from? - I’m from ..."}', '{"text": "欣赏视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。同时，试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (6, '{"text": "在歌曲中复现重点句型-Where are you from? - I’m from ..."}', '{"text": "欣赏视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。同时，试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (15, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看，认真听，根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (16, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，每句之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (17, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型Where are you from? - I’m from ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第7页，朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (18, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 3个人一组\n2. 从课文角色（Mr Jones, Wu Yifan, Amy）中选1个角色\n3. 仿照课文图，进行角色对话\n4. 完成后，3人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动\n3. 表演结束后，我会请你们上台来展示"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (19, '{"text": "识记词汇she, student, pupil, he, teacher，并了解其在语境中的应用"}', '{"text": "1. 看图片猜测词义，查看中文释义确认词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习5个单词。根据图片猜一猜它们的意思！注意看单词、认真听录音，并小声跟读。"}', '{"text": "1. 依次呈现单词卡片，并且根据学生的反应，进行必要的重复呈现\n2. 引导学生借助图片理解词义，不能理解的单词，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (20, '{"text": "通过人际活动巩固学习Let’s talk部分的核心句型：- Where are you from? -I’m from ..."}', '{"text": "1. 看图中的国旗，说出所代表国家的国名。重复数次，直至熟练。\n2. 读出小熊对话框里的句子。重复数次，直至熟练。\n3. 四个人一组，分别扮演这四个国家的标志动物。套用小熊句型，自我介绍，然后询问下一个。\n4. 下一个介绍自己，然后询问再下一个。这样接续玩下去。\n5. 交换角色，继续活动。"}', '{"text": "1. 请把课本翻到第7页，看下面的活动\n2. 同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 将全班同学进行分组，每组四个人\n3. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (21, '{"text": "学生通过对歌谣的理解和朗读操练巩固四个核心词汇：he, she, teacher, student"}', '{"text": "1. 聆听录音\n2. 看着文字，跟读\n3. 朗读韵文\n4. 重复1-3步，直至朗朗上口"}', '{"text": "我们放松一下，学一首英文歌谣。大家要认真听、认真唱！看看谁唱得最好！"}', '{"text": "1. 按照活动步骤带领学生学唱歌谣\n2. 巡视并监督学生认真学唱"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (22, '{"text": "Read, listen and chant：学习字母a在单词中的发音，即短元音/æ/\n Read, listen and circle:通过听音圈词活动，帮助学生进一步体会字母a在单词中的发音\n Listen and write:通过描红单词初步掌握单词书写的要求并辨认词形"}', '{"text": "Read, listen and chant：\n1. 阅读指示语，理解题目要求。\n2. 聆听录音，跟读每个单词。然后独立大声读出每个单词。\n3. 观看视频，学唱歌谣。\nRead, listen and circle:\n1. 读各组单词。\n2. 听录音，并在课本上圈出所听到的那个单词。\n3. 再聆听录音，确认。\nListen and write:\n1. 聆听录音，模仿。\n2. 描课本上的白线单词。临摹书写范例，抄写单词。"}', '{"text": "Read, listen and chant:\n1. 请把课本翻到第6页，看上面的活动\n2. 跟读四个单词，并体会字母a在单词中的发音\n3. 跟唱歌谣，要认真听、认真唱哦\nRead, listen and circle:\n1. 请看课本第6页中间的活动\n2. 同活动步骤\nListen and write:\n1. 请看课本第6页下面的活动\n2. 同活动步骤"}', '{"text": " Read, listen and chant：\n1. 带领学生理解题目要求\n2. 播放单词音频指导学生跟读\n3. 播放歌谣音频指导学生跟读跟唱\nRead, listen and circle:\n1. 引导学生先拼读四组单词\n2. 播放录音，要求学生圈出所听到的单词\n3. 针对错误率较高的题目，重复播放音频供学生反复听练\nListen and write:\n1. 组织学生听音模仿，并播放录音\n2. 监督并指导学生临摹、书写规范"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (23, '{"text": "通过图文匹配，强化学生对he和she用法的区别"}', '{"text": "准备：事先预备一张或数张你的老师或朋友的照片\n1. 读指示语，理解题目要求。\n2. 读范例对话框，观察划勾的句子，明白应该给什么样的句子划勾。与同伴讨论确认。\n3. 读图片，按范例的提示，给其中一个句子划勾。\n4. 拿出事先准备好的照片，进行介绍。"}', '{"text": "1. 请把课本翻到第9页\n2. 同活动步骤"}', '{"text": "1. 提前通知学生准备一张或数张他/她的老师或朋友的照片\n2. 带领学生按活动步骤进行活动\n3. 点击答案，让学生核对答案"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (24, '{"text": "通过听力活动，检测评价学生的学习效果"}', '{"text": "1. 读指示语，理解题目要求。\n2. 观察图，理解每一幅图的意思。\n3. 聆听录音，按要求标序号。"}', '{"text": "1. 请把课本翻到第10页，看上面的活动\n2. 同活动步骤"}', '{"text": "1. 带领学生理解题目要求\n2. 播放录音，并监督学生认真完成练习\n3. 针对错误率较高的题目，重复播放音频供学生反复听练\n4. 点击答案，让学生核对答案"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (25, '{"text": "通过图文连线活动，检测评价学生的学习效果"}', '{"text": "1. 观察图里的人物，想一想他们的职业和性别等，用英语说出来。\n2. 读图中的6个单词，大声读出来。\n3. 把单词与图里的人物进行连线匹配。\n4. 与同伴核对"}', '{"text": "1. 请看课本第10页中间的活动\n2. 同活动步骤"}', '{"text": "1. 带领学生理解题目要求\n2. 监督学生认真完成练习\n3. 点击答案，让学生核对答案"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (26, '{"text": "通过活泼、欢快的歌曲，复习巩固所学核心词汇和句型"}', '{"text": "1. 读歌词数遍，直至流畅。\n2. 聆听歌曲录音。\n3. 模仿、跟唱。"}', '{"text": "我们放松一下，学一首英文歌曲。大家要认真听、认真唱！看看谁唱得最好！"}', '{"text": "1. 按照活动步骤带领学生学唱歌曲\n2. 巡视并监督学生认真学唱"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (27, '{"text": "通过一个有趣味又有意义的故事，增加学生语言的输入并引导学生逐步适应文本阅读"}', '{"text": "1. 看教材P11-Story time板块，观察图。\n2. 听录音，看图。\n3. 听录音，同步默读相应文字。\n4. 阅读每幅图上的文字，结合图进一步理解意思。\n5. 与同伴谈论故事内容。试着讲故事。\n6. 看视频，完成拼图游戏。"}', '{"text": "1. 下面我们读一个有趣的小故事。故事里，Zip和Zoom正在玩“老师和学生”的游戏。他们两个都想当老师。谁会成为老师呢？让我们开始阅读吧！\n2. 同活动步骤。"}', '{"text": "1. 按照活动步骤带领学生阅读和欣赏故事\n2. 引导学生借助图片猜测故事内容\n3. 巡视并监督学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (28, '{"text": "以歌曲的形式介绍家庭成员，使学生对即将学习的My family产生兴趣和求知欲"}', '{"text": "欣赏视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。同时，试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (29, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看，认真听，根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (30, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，每句之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句义，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (31, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型-Who’s that man/woman? - He’s/She’s my ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第12页，朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (32, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 2个人一组\n2. 从课文的三组角色（第一组：Zoom, Zip；第二组：Sarah, Zhang Peng; 第三组：Chen Jie, Mike）中各选1个角色\n3. 仿照课文图，进行角色对话\n4. 完成后，2人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动\n3. 表演结束后，我会请你们上台来展示"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (33, '{"text": "在情景剧中复现重点句型-Who’s ...? - He’s/She’s my ..."}', '{"text": "欣赏视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频。同时，试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (34, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看，认真听，根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (35, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，每句之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (36, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型- Who’s that man? - He’s my ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第14页，朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (37, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 3个人一组\n2. 从课文角色（Chen Jie, Amy, Dad）中选1个角色\n3. 仿照课文图，进行角色对话\n4. 完成后，3人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动\n3. 表演结束后，我会请你们上台来展示"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (38, '{"text": "识记词汇father, dad, man, mother, mum, woman，并了解其在语境中的应用"}', '{"text": "1. 看图片理解词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习6个单词。根据图片猜一猜它们分别是什么意思！注意看单词、认真听录音，并小声跟读。"}', '{"text": "1. 依次呈现单词卡片，并且根据学生的反应，进行必要的重复呈现\n2. 引导学生借助图片理解词义，不能理解的单词，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (39, '{"text": "通过人际活动巩固学习Let’s talk部分的核心句型：- Who’s that man/woman? - He’s/She’s my ..."}', '{"text": "准备：个人家庭相簿或几张家庭成员照片。\n1. 甲乙两人一组。甲拿着自己家庭相簿。\n2. 乙指着甲的相簿，用句型Who’s that man/woman?询问这是谁。\n3. 甲用句型He’s/She’s my ...回答。\n4. 交换角色。甲问乙答。"}', '{"text": "1. 请把课本翻到第14页，看下面的活动\n2. 同活动步骤"}', '{"text": "1. 活动前督促学生准备好个人家庭相簿或几张家庭成员照片\n2. 带领学生理解活动步骤\n3. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (40, '{"text": "学生通过歌谣巩固操练家庭成员father, mother, son等的音和义，并初步感知形容词tall和small等"}', '{"text": "1. 聆听录音\n2. 看着文字，跟读\n3. 朗读韵文\n4. 重复1-3步，直至朗朗上口"}', '{"text": "我们放松一下，学一首英文歌谣。大家要认真听、认真唱！看看谁唱得最好！"}', '{"text": "1. 按照活动步骤带领学生学唱歌谣\n2. 巡视并监督学生认真学唱"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (41, '{"text": "在歌曲中再次复现重点词汇father, mother, brother, sister, grandmother, grandfather，并传递爱家、关爱家庭成员的情感态度"}', '{"text": "欣赏视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。同时，试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (42, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看，认真听，根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (43, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，每句之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (44, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型①- Who’s that ...? - He’s/She’s my ... ②- Is he/she your ...? -Yes, he/she is. 和- No, he/she isn’t."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第17页，朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (45, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 2个人一组\n2. 1人扮演Amy，1人扮演Sarah\n3. 仿照课文图，进行角色对话\n4. 完成后，2人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动\n3. 表演结束后，我会请你们上台来展示"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (46, '{"text": "识记词汇sister, brother, grandmother, grandma, grandfather, grandpa，并了解其在语境中的应用"}', '{"text": "1. 看图片猜测词义，查看中文释义确认词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习6个单词。根据图片猜一猜它们的意思！注意看单词、认真听录音，并小声跟读。"}', '{"text": "1. 依次呈现单词卡片，并且根据学生的反应，进行必要的重复呈现\n2. 引导学生借助图片理解词义，不能理解的单词，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (47, '{"text": "通过人际活动巩固学习Let’s talk部分的核心句型：①Who’s that ...? ②-Is he/she your ...? - No, he/she isn’t."}', '{"text": "准备：每人准备家人照片1-2张。\n1. 熟读对话框中的句子。\n2. 两人一组，仿照对话活动。\n①甲呈现手中照片。\n②乙问“Who’s that ...?”(boy/girl/man/woman)\n③甲说“Guess.”(意思是“猜。”)\n④乙猜“Is he/she your ...?”(brother/sister/father/mother…)\n⑤如果猜对了，甲说“Yes, he’s/she’s my ...”。如果猜错了，甲说“No, he/she isn’t. He’s/She’s my ...”(brother/sister/father/mother)\n3. 交换角色。重复①-⑤。"}', '{"text": "1. 请把课本翻到第17页，看下面的活动\n2. 同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (48, '{"text": "复习单词grandfather/grandpa, grandmother/grandma, sister, brother，巩固family的概念"}', '{"text": "1. 聆听录音\n2. 看着文字，跟读\n3. 朗读韵文\n4. 重复1-3步，直至朗朗上口"}', '{"text": "我们放松一下，学一首英文歌谣。大家要认真听、认真唱！看看谁唱得最好！"}', '{"text": "1. 按照活动步骤带领学生学唱歌谣\n2. 巡视并监督学生认真学唱"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (49, '{"text": "Listen, repeat and chant:学习字母e在单词中的发音，即短元音/e/\nRead, listen and circle:通过拼读和听音圈词活动，帮助学生进一步体会字母e在单词中的发音\nListen and write:通过描红单词初步掌握单词书写的要求并辨认词形"}', '{"text": "Listen, repeat and chant：\n1. 聆听录音。\n2. 看着单词，跟读。\n3. 朗读单词。\n4. 反复读，直至熟练，注意字母e的发音。\n5. 观看视频，学唱歌谣。\nRead, listen and circle:\n1. 读各组单词。\n2. 听录音，并在课本上圈出所听到的那个单词。\n3. 再聆听录音，确认。Listen and write:\n1. 聆听录音，模仿。\n2. 描课本上的白线单词。\n3. 在范例单词的后面，临摹抄写这个单词。"}', '{"text": "Listen, repeat and chant：\n1. 请把课本翻到第16页，看上面的活动\n2. 跟读四个单词，并体会字母e在单词中的发音\n3. 跟唱歌谣，要认真听、认真唱哦\nRead, listen and circle:1. 请看课本第16页中间的活动\n2. 同活动步骤\nListen and write:\n1. 请看课本第16页下面的活动\n2. 同活动步骤"}', '{"text": "Listen, repeat and chant：\n1. 播放单词音频指导学生跟读\n2. 播放歌谣音频指导学生跟读跟唱\nRead, listen and circle:\n1. 引导学生先拼读四组单词\n2. 播放录音，要求学生圈出所听到的单词\n3. 针对错误率较高的题目，重复播放音频供学生反复听练\nListen and write:\n1. 组织学生听音模仿，并播放录音\n2. 监督并指导学生临摹、书写规范"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (50, '{"text": "在图文匹配活动中，复现核心句型This is my ..."}', '{"text": "1. 观察照片，确定人物的家庭身份。\n2. 读男孩对话框的内容。\n3. 逐个读出黄色条框中的句子。把每个句子与照片进行连线匹配。"}', '{"text": "1. 请把课本翻到第19页\n2. 同活动步骤"}', '{"text": "1. 带领学生按活动步骤进行活动\n2. 点击答案，让学生核对答案"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (51, '{"text": "通过听力活动，检测核心句型①- Who is that man/woman? - He’s/She’s my ... ②- Is he/she your …? - No, he/she isn’t./Yes, he/she is.的理解和掌握"}', '{"text": "1. 看图，搞清楚每幅图中被介绍的人是谁。\n2. 听录音，根据听到内容的先后，把序号写在各图左下角的便签纸上。\n3. 与同伴核对，然后点击答案确认。"}', '{"text": "1. 请把课本翻到第20页，看上面的活动\n2. 同活动步骤"}', '{"text": " 1. 带领学生理解题目要求（听音排序）\n2. 播放录音，并监督学生认真完成练习\n3. 针对错误率较高的题目，重复播放音频供学生反复听练\n4. 点击答案，让学生核对答案"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (52, '{"text": "通过看图判读活动，检测核心词汇mother, brother, grandfather, grandmother的掌握情况"}', '{"text": " 1. 看图，看清楚图中人物的家庭身份。\n2. 读图下面的A、B两个选项中的文字。\n3. 根据图中人物，选择选项，在所选项前面的方框内画勾。"}', '{"text": "1. 请看课本第20页中间的活动\n2. 同活动步骤"}', '{"text": "1. 带领学生理解题目要求\n2. 监督学生认真完成练习\n3. 点击答案，让学生核对答案"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (53, '{"text": "通过活泼、欢快的歌曲，复习巩固所学核心词汇"}', '{"text": "1. 读歌词数遍，直至流畅。\n2. 聆听歌曲录音。\n3. 模仿、跟唱。"}', '{"text": "我们放松一下，学一首英文歌曲。大家要认真听、认真唱！看看谁唱得最好！"}', '{"text": "1. 按照活动步骤带领学生学唱歌曲\n2. 巡视并监督学生认真学唱"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (54, '{"text": "通过一个有趣味又有意义的故事，增加学生语言的输入并引导学生逐步适应文本阅读"}', '{"text": "1. 看教材P21-Story time板块，观察图。\n2. 听录音，看图。\n3. 听录音，同步默读相应文字。\n4. 阅读每幅图上的文字，结合图进一步理解意思。\n5. 与同伴谈论故事内容。试着讲故事。\n6. 看视频，完成拼图游戏。"}', '{"text": "1. 下面我们读一个有趣的小故事。故事里，Zip到Zoom家做客的时候，Zoom介绍他的妈妈是演员，正当他穿上妈妈的衣服表演时，发生了一件尴尬的事情。到底发生了什么？让我们开始阅读吧！\n2. 同活动步骤。"}', '{"text": "1. 按照活动步骤带领学生阅读和欣赏故事\n2. 引导学生借助图片猜测故事内容\n3. 巡视并监督学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (55, '{"text": "以话题“学校生活”作为切入点，使学生对本单元内容产生兴趣和求知欲"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。并试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (56, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看、听，并根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (57, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，句子之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (58, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型①- Where is ...? - It’s ... ②- Is this/that. ..? - Yes, it is./No, it isn’t."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第2页，大声朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (59, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 3个人一组\n2. 从课文的两组角色（第一组：Chen Jie, Amy, Zhang Peng；第二组：Wu Yifan, Sarah, John）中各选1个角色\n3. 仿照课文图，进行角色对话\n4. 完成后，3人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组进行表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (60, '{"text": "在歌曲中复现句型- Where is ...? - It is on ...，使学生感受语言的工具性和艺术性"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。并试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (61, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看、听，并根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (62, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，句子之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (63, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型①- Where is ...? - It’s ... ②- Is this/that. ..? - Yes, it is./No, it isn’t."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第4页，大声朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (64, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 2个人一组\n2. 1人扮演Mike，另一人扮演老师\n3. 仿照课文图，进行角色对话\n4. 完成后，2人交换角色，再进行一轮对话"}', '{"text": "1. 同学们，我们来分组进行表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (65, '{"text": "识记词汇second floor, first floor, teachers’ office, library并了解其在语境中的应用"}', '{"text": "1. 看图片理解词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆；\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习4个单词。根据图片猜一猜它们的意思！注意看单词、认真听录音，并小声跟读。"}', '{"text": "1. 依次呈现单词卡片，并且根据学生的反应，进行必要的重复呈现\n2. 引导学生借助图片理解词义，不能理解的单词，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (66, '{"text": "通过人际活动巩固学习Let’s talk部分的核心句型：①-Where is ...? - It’s ... ②-Is this/that ..? -Yes, it is./No, it isn’t."}', '{"text": "1. 看课本P4下-Look, ask and answer板块。\n2. 看图，搞清楚各层楼有什么设施，在什么地方。\n3. 熟读对话框里的句子。\n4. 看图，仿照对话框里的句子，两人一组，作问答练习。"}', '{"text": "同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (67, '{"text": "操练Let’s learn板块中有关学校场馆名称的单词及词组（library, teachers’ office）"}', '{"text": "1. 读出每幅图下面的句子。\n2. 两人一组，一个人按照图下面的句子发指令，另一个人做动作。\n3. 四幅图动作都完成以后，交换角色。"}', '{"text": "1. 请看教材P5下-Let’s do板块\n2. 同活动步骤"}', '{"text": "1. 带领学生跟读句子，并且根据学生的反应，进行必要的重复播放\n2. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (68, '{"text": "以歌曲的形式帮助学生巩固新学语言知识，调节课堂气氛，提高学生的学习兴趣"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。并试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (69, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看、听，并根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (70, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，句子之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (71, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型①- Is that ...? - No, it isn''t. ②- Do you have ...? - Yes, we do."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第7页，大声朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (72, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 2个人一组\n2. 1人扮演Chen Jie，另1人扮演记者\n3. 仿照课文图，进行角色对话\n4. 完成后，2人交换角色，再进行一轮对话"}', '{"text": "1. 同学们，我们来分组进行表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (94, '{"text": "在歌曲中复现重点句型- What time is it? - It''s ... o''clock.，进一步加深印象"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。并试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (73, '{"text": "识记词汇computer room, art room, music room, playground并了解其在语境中的应用"}', '{"text": "1. 看图片理解词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习4个单词。根据图片猜一猜它们的意思！注意看单词、认真听录音，并小声跟读。"}', '{"text": "1. 依次呈现单词卡片，并且根据学生的反应，进行必要的重复呈现\n2. 引导学生借助图片理解词义，不能理解的单词，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (74, '{"text": "通过人际活动巩固学习Let’s talk部分的核心句型：①- Do you have ...? - Yes, we do./No, we don’t. ②- Where is ...? - It’s ..."}', '{"text": "准备：做些卡片，每个卡片上写一个房间或设施名称。\n1. 注意图右上方思维图里的楼层及房间示意图，说出房间名称和楼层。\n2. 读对话框里的句子。\n3. 以卡片上写的房间或设施为假设对象，仿照对话框的句子，两人一组做问答练习。"}', '{"text": "同学们，我们一起来做个游戏好不好？请看教材P7下-Let’s play板块。"}', '{"text": "1.活动前督促学生准备好卡片，并摆好学校结构图\n2.带领学生理解活动步骤\n3.巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (75, '{"text": "通过人际活动巩固学习Let’s learn部分的词汇（computer room, art room, music room，playground）和句型Do you have ...?"}', '{"text": "仍旧利用上一个游戏活动写着房间或设施名称的卡片。\n1. 仔细看图，从窗子可以判断出每个房间的用途，说出是什么房间。\n2. 读图中对话框的句子。\n3. 以卡片上的房间或设施为对象，套用对话框中的句子，进行问答活动。"}', '{"text": "1. 请看教材P8下- Look, ask and answer板块。\n2. 同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (76, '{"text": "Read, listen and chant:通过听读活动，让学生感知er在单词中的发音规则\nRead, underline and say:强化记忆er的发音规则，根据er的发音规则读出新词\nLook, listen and write:根据单词的读音拼写出符合er发音规则的单词"}', '{"text": "Read, listen and chant：\n1. 阅读指示语，理解题目要求。\n2. 聆听录音，跟读每个单词。然后独立大声读出每个单词，关注标红的字母的读音。\n3. 观看视频，学唱歌谣。\nRead, underline and say:\n1. 看课本P6-Let’s spell板块中的Read, underline and say.活动。\n2. 读各组单词。\n3. 注意第一个单词中下划线字母的读音。在每个单词中与之读音相同的字母下，划线。\n4. 大声读出每个单词，注意下划线部分的读音。\n5. 与同伴互相聆听和矫正。\nLook, listen and write:\n1. 看课本P6-Let’s spell板块中的Look, listen and write.活动。\n2. 看下面的图和带有空白线的句子。结合图片猜一下空白线上应该填什么单词。\n3. 听录音，依据录音内容填充空白。\n4. 再听录音，核对答案。\n5. 与同伴交流。然后，确认答案。"}', '{"text": "Read, listen and chant：\n1. 请把课本翻到第6页，看上面的活动\n2. 跟读四个单词，并体会er在单词中的发音规则\n3. 跟唱歌谣，要认真听认真唱哦\nRead, underline and say:\n同活动步骤\nLook, listen and write:\n同活动步骤"}', '{"text": "Read, listen and chant：\n1. 带领学生理解题目要求（读一读，听一听，唱一唱）\n2. 引导学生听读单词，并感知er在单词中的发音规则\n3. 播放歌谣，指导学生跟读跟唱\nRead, underline and say:\n1. 引导学生读单词，引导他们感知er在单词中的发音规则\n2. 点击答案，让学生核对。要引导学生之间互帮互助，以消化理解回答错误的题目\nlisten and write:\n1. 引导学生观察图片，并猜测对应短语或句子的意思\n2. 听录音时，引导学生根据er发音规则拼写出单词\n3. 点击答案，让学生核对。要引导学生之间互帮互助，以消化理解回答错误的题目"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (77, '{"text": "Read and fill in the table：使学生读懂四组句子，并按照读到的信息正确填充建筑结构图。\nLook and write:使学生观察句子的书写规范，并进行仿写"}', '{"text": "Read and fill in the table：\n1. 看课本P9-Read and write板块中的Read and fill in the table.活动。\n2. 阅读指示语，理解题目要求。\n3. 看建筑结构图，搞清楚已有房间是什么房间，在各层楼的什么地方。\n4. 读图下面第1号板块的信息，用铅笔把library的第一个字母“L”标在你认为可能的那间房里。\n5. 用同样的方法处理第2、3、4 各板块。如果发现原来的标识不合适，就用橡皮擦掉，重新调整、标注。直到每个房间的位置都正确。\n6. 与同伴交流。然后，确认答案。\nLook and write:\n1. 看课本P9-Read and write板块中的Look and write.活动。\n2. 阅读指示语，理解题目要求。\n3. 看左边的图，辨认并用英语说出是什么地方。\n4. 读一读四线格中的句子，比较上下两句在书写上的不同，并指出书写不规范的那一句及问题所在。（提示：书写时应在单词间空一格。）\n5. 仿照图右边的书写范例，在下面的空白线上抄写句子。注意单词间需要空格，且句首字母需要大写，句尾需要加上句号。"}', '{"text": "同活动步骤"}', '{"text": "Read and fill in the table：\n1. 带领学生理解活动步骤\n2. 点击答案，让学生核对。要引导学生之间互帮互助，以消化理解回答错误的题目\nLook and write:\n1. 引导学生观察例句中单词的间隔，并提示书写规范。\n2. 指导并监督学生认真、规范书写。"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (78, '{"text": "Listen and number:通过听力活动，检测本单元A, B部分的核心句型\nLook and circle:通过看图圈出正确的单词活动，考查本单元核心词汇的认读"}', '{"text": "Listen and number：\n1. 看课本P10上-Let’s check板块中的Listen and number.活动。\n2. 阅读指示语，理解题目要求。\n3. 看图，搞清楚每幅图是什么房间。\n4. 聆听录音，按照指示语要求做。\n5. 与同伴核对答案并交流。然后，确认答案。\nLook and circle:\n1. 看课本P10上-Let’s check板块中的Look and circle.活动。\n2. 观察右边的图，搞清楚每间房子是做什么用的，用英语说出每间房子的名称。\n3. 读左边A、B两组房间布局示意图及房间名称，并与右图对照。圈出与图相符的那一组的标号。\n4. 与同伴核对答案并交流。然后，确认答案。"}', '{"text": "同活动步骤"}', '{"text": "Listen and number：\n1. 带领学生理解题目要求\n2. 播放录音，并监督学生认真完成练习\n3. 针对错误率较高的题目，重复播放音频供学生反复听练\n4. 点击答案，让学生核对。要引导学生之间互帮互助，以消化理解回答错误的题目\nLook and circle:\n1. 带领学生理解题目要求\n2. 监督学生认真完成练习\n3. 点击答案，让学生核对。要引导学生之间互帮互助，以消化理解回答错误的题目"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (79, '{"text": "帮助学生巩固新学语言知识并复习旧知识；调节课堂气氛，提高学生的学习兴趣"}', '{"text": "1. 读歌词数遍，直至流畅。\n2. 聆听歌曲录音。\n3. 模仿、跟唱。"}', '{"text": "我们放松一下，学一首英文歌曲。大家要认真听、认真唱！看看谁唱得最好！"}', '{"text": "1. 按照活动步骤带领学生学唱歌曲\n2. 巡视并监督学生认真学唱"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (95, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看、听，并根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (80, '{"text": "通过一个有趣味又有意义的故事，增加学生语言的输入并引导学生逐步适应文本阅读"}', '{"text": "1. 看教材P11-Story time板块，观察图。\n2. 听录音，看图。\n3. 听录音，同步默读相应文字。\n4. 阅读每幅图上的文字，结合图进一步理解意思。\n5. 与同伴谈论故事内容。试着讲故事。\n6. 看视频，完成拼图游戏。"}', '{"text": "1. 下面我们读一个有趣的小故事。Zip和Zoom去参观一所学校。河马老师是他们的“导游”，负责回答他们的询问并介绍学校教室、场馆及其位置。我们跟随河马老师来看看这所学校吧。\n2. 同活动步骤。"}', '{"text": "1. 按照活动步骤带领学生阅读和欣赏故事\n2. 巡视并监督学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (81, '{"text": "通过视听活动，使学生对本课要学的重点句型- What time is it? -It''s...o''clock.产生兴趣"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。并试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (82, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看、听，并根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (83, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，每句之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句义，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (84, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型① - What time is it? - It''s ...o''clock. ② It’s time to/for ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第12页，大声朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (85, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 2个人一组\n2. 2人依次轮流扮演课文图中的讲话角色\n3. 仿照课文图，进行角色对话\n4. 完成后，2人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组进行表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动\n3. 表演结束后，我会请你们上台来展示"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (86, '{"text": "在歌曲中复现句型- What time is it? - It''s ... o''clock.使学生感受语言的工具性和艺术性"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。并试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (87, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看、听，并根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (88, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，句子之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (89, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型①- What time is it? - It''s ... o''clock. ②- It’s time for ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第14页，大声朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (90, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 3个人一组\n2. 1人扮演John，1人扮演Wu Yifan，第3个人扮演学校员工\n3. 仿照课文图，进行角色对话\n4. 完成后，3人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组进行表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (91, '{"text": "识记词汇breakfast、dinner、English class、lunch、music class、PE class，并了解其在语境中的应用"}', '{"text": "1. 看图片理解词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆；\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习6个单词。根据图片猜一猜它们的意思！注意看单词、认真听录音，并小声跟读。"}', '{"text": "依次呈现单词卡片，并且根据学生的反应，进行必要的重复呈现"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (92, '{"text": "了解不同时区存在时差的常识并在真实交流中操练巩固句型What time is it?"}', '{"text": "准备：按照课本图示，画四个表盘，表示北京、纽约、伦敦和悉尼同一时段不同时区的不同时间。\n1. 注意四个表盘上时间的不同，读出每个表盘上的时间。\n2. 读对话框里的句子。\n3. 用表盘上的时间和相应的活动替换对话框里相应部分。\n4. 两人一组，用上面提供的信息，套用对话框句型进行对话。"}', '{"text": "1. 请看教材P14下-Let’s play板块\n2. 同活动步骤"}', '{"text": "1. 带领学生按活动步骤进行活动\n2. 引导学生模仿对话进行替换练习"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (93, '{"text": "操练Let’s learn板块中有关课程和三餐的单词及词组"}', '{"text": "1. 读出每个蓝色块里的句子。反复读其中新的表达方式：\ndrink some milk (喝点牛奶)\nhave some chicken (吃点鸡肉)\neat some rice (吃点米饭)\njump and run (跑跑跳跳)\nread and write (读书写字)\nsing and dance (唱歌跳舞)\n2. 下面有两项活动建议，任你选择：\n活动建议A：小组为单位，一个人按照蓝色块里的句子发指令，其他人做动作。六个色块的动作都完成以后，换人发令。\n活动建议B：两人一组，一个人按照色块里的句子发指令，另一个人做动作。六个色块的动作都完成以后，交换角色。"}', '{"text": "1. 同学们，我们来玩一个游戏好不好？\n2. 请看教材P14下-Let’s play板块\n3.  同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 带领学生跟读句子，并且根据学生的反应，进行必要的重复播放\n3. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (96, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，每句之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (97, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型①- What time is it? - It’s ... o’clock. ②It’s time to/for ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第17页，大声朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (98, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 3个人一组\n2. 1人扮演Mike，1人扮演妈妈，另1人扮演老师\n3. 仿照课文图，进行角色对话\n4. 完成后，3人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组进行表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (99, '{"text": "识记词汇get up, go to school, go home, go to bed并了解其在语境中的应用"}', '{"text": "1. 看图片理解词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习4个词组。根据图片猜一猜它们分别是什么意思？注意看单词、认真听录音，并小声跟读。"}', '{"text": "1. 依次呈现单词卡片，并且根据学生的反应，进行必要的重复呈现\n2. 引导学生借助图片理解词义，不能理解的单词，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (100, '{"text": "操练句型①- What time is it? - It''s ... ②It’s time for/to ..."}', '{"text": "准备：每人做一个表盘，画出某个与作息活动有关的时间。（如果把表针做成能转动的，就更好了。）\n1. 大声读出对话框里的文字。\n2. A、B两人一组。按照图示，套用对话框句型，进行对话活动：A用表盘出示某个时间，并提问；B回答。\n3. 交换角色，继续活动。"}', '{"text": "1. 请看教材P17下-Let’s play板块\n2. 同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 指导学生使用“活动目的”中提及的重点句型\n3. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (101, '{"text": "操练句型①- What time is it? - It''s ... ②It’s time for/to ...和词汇get up, breakfast, go to school, English class, lunch, music class, PE class, go home, go to bed"}', '{"text": "1. 个人阅读左侧Monday的作息表，大声读出每个时间和每项活动。\n2. 个人大声读出对话框里的句子。\n3. A、B、C三人一组，仿照课文图示进行活动：\nA问时间（What time is it?）。\nB说出时间（It’s ...）。\nC说这个时间的活动（It’s time ...）。\nA表演出这个活动。\n交换角色，使每位学生都轮流扮演A、B、C。\n（此活动可以加入竞赛成分，比如某位学生说不出，或表演不准确，都可以扣分。被扣分少者为胜。）"}', '{"text": "1. 请看教材P18下-Let’s play板块\n2. 同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 组织学生分组\n3. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (102, '{"text": "Read, Listen and chant:通过听读活动，让学生感知字母组合ir和ur在单词中的发音规则\nRead, listen and circle:强化记忆ir和ur的发音规则，巩固学习ir和ur的音——形对应关系\nLook, listen and write:根据单词的读音拼写出符合ir和ur发音规则的单词"}', '{"text": "Read, Listen and chant：\n1. 聆听录音，跟读每个单词。然后独立大声读出每个单词。\n2. 反复读，直至脱口而出。注意标红的字母组合。\nRead, listen and circle:\n1. 看课本P16-Let’s spell板块中的Read, listen and circle.活动。\n2. 读1-4各组单词。\n3. 听录音，圈出每组中所听到的那个单词。\n4. 再聆听录音，确认。\n5. 与同伴核对答案并交流。然后，确认答案。\nLook, listen and write:\n1. 看课本P16-Let’s spell板块中的Look, listen and write.活动。\n2. 看图、聆听录音，模仿。\n3. 读文字。\n4. 听录音并将所缺单词填在横线上。注意：没有学过的单词按照所学的发音规则进行拼写。"}', '{"text": "Read, Listen and chant：\n1. 请把课本翻到第16页，看上面的活动\n2. 跟读四个单词，并体会字母组合ir和ur在单词中的发音规则\n3. 跟唱歌谣，要认真听认真唱哦\nRead, listen and circle:同活动步骤\nLook, listen and write:同活动步骤"}', '{"text": "Read, Listen and chant：\n1. 带领学生理解题目要求\n2. 引导学生读单词，并感知字母组合ir和ur在单词中的发音规则。\n3. 播放歌谣，指导学生跟读跟唱\nRead, listen and circle:\n1. 带领学生理解题目要求（读一读，听一听，圈一圈）\n2. 引导学生读单词，引导他们感知ir和ur在单词中的发音规则\n3. 点击答案，让学生核对。要引导学生之间互帮互助，以消化理解回答错误的题目\nLook, listen and write:\n1. 引导学生观察图片，并猜测对应短语的意思\n2. 听录音时，引导学生根据ir和ur发音规则拼写出单词\n3. 点击答案，让学生核对。要引导学生之间互帮互助，以消化理解回答错误的题目"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (103, '{"text": "Read and number：能在图片的帮助下读懂四组句子并完成选择正确图片的任务；能按意群和正确的语音语调朗读句子\nLook and write:能根据实际情景仿写本单元核心句型- What time is it? - It’s ... o''clock."}', '{"text": "Read and number:\n1. 看课本P19-Read and write板块中的Read and number.活动。\n2. 大声读出每个色块上的文字。\n3. 观察各图里人物在做什么活动。\n4. 把各图活动与各段文字内容进行匹配。\n5. 把相应文字的标号填在相应各图的圆圈里。\n6. 与同伴交流。然后，确认答案。\nLook and write:\n1. 看课本P19-Read and write板块中的Look and write.活动。\n2. 观察表盘，读出时间。\n3. 大声读出四线三格上的句子。\n4. 观察每个句子的书写法，尤其注意每个句子中首字母要大写。\n5. 在Zip时钟上添加指针，然后读出时间。\n6. 仿照三格四线上的句子，按照Zip时钟的时间，在下面的空格里写句子"}', '{"text": "同活动步骤"}', '{"text": "Read and number:\n1. 带领学生按活动步骤进行活动\n2. 点击答案，让学生核对。要引导学生之间互帮互助，以消化理解回答错误的题目\nLook and write:\n1. 带领学生按活动步骤进行活动\n2. 指导学生进行正确仿写、规范书写"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (118, '{"text": "识记词汇do morning exercises, eat breakfast, have ... class, play sports, eat dinner并了解其在语境中的应用"}', '{"text": "1. 看图片理解词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习5个词组。根据图片猜一猜它们的意思！注意看词组、认真听录音，并小声跟读"}', '{"text": "1. 依次呈现单词卡片，并且根据学生的反应，进行必要的重复呈现\n2. 引导学生借助图片理解词义，不能理解的单词，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (104, '{"text": "Listen and number:通过听力活动，检测本单元A, B部分的核心句型\nLook and match:\n通过看时间匹配活动，考查单元核心词汇的认读和意义的理解"}', '{"text": "Listen and number：\n1. 看课本P20上-Let’s check板块中的Listen and number.活动。\n2. 观察图里面人物在做什么。\n3. 读出每幅图上方的时间。\n4. 听录音。根据听到内容的先后，把序号写在各图下方的圆圈里。\n5. 与同伴核对答案并交流。然后，确认答案。\nLook and match:\n1. 看课本P20上-Let’s check板块中的Look and match.活动。\n2. 观察7个表盘，大声读出表盘上的时间。\n3. 读下面7个字条上的文字，大声读出来。\n4. 把表盘上的时间与字条上的活动进行匹配，把表盘上的编号写在字条前面的圆圈里。\n5. 与同伴核对答案并交流。然后，确认答案。"}', '{"text": "同活动步骤"}', '{"text": "Listen and number:\n1. 带领学生理解题目要求\n2. 播放录音，并监督学生认真完成练习\n3. 针对错误率较高的题目，重复播放音频供学生反复听练\n4. 点击答案，让学生核对。要引导学生之间互帮互助，以消化理解回答错误的题目"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (105, '{"text": "帮助学生巩固新学语言知识或复习旧知识；调节课堂气氛，提高学生的学习兴趣"}', '{"text": "1. 看课本P20下-Let’s sing板块。\n2. 读歌词数遍，直至流畅。\n3. 聆听歌曲录音。\n4. 模仿、跟唱。"}', '{"text": "我们放松一下，学一首英文歌曲。大家要认真听、认真唱！看看谁唱得最好！"}', '{"text": "1. 按照活动步骤带领学生学唱歌曲\n2. 巡视并监督学生认真学唱"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (106, '{"text": "通过一个有趣味又有意义的故事，增加学生语言的输入并引导学生逐步适应文本阅读"}', '{"text": "1. 看课本P21-Story time板块，观察图。\n2. 听录音，看图。\n3. 听录音，同步默读相应文字。\n4. 阅读每幅图上的文字，结合图进一步理解意思。\n5. 与同伴谈论故事内容。试着讲故事。\n6. 看视频，完成拼图游戏。"}', '{"text": "1. 下面我们读一个有趣的小故事。愚人节（4月1日）早上，Zip故意将一个时钟拨快了一个小时，提前叫醒Zoom起床，然后祝Zoom愚人节快乐。让我们看看Zip到底是怎么给Zoom“过节”的！\n2. 同活动步骤"}', '{"text": "1. 按照活动步骤带领学生阅读和欣赏故事\n2. 巡视并监督学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (107, '{"text": "以话题“日常生活”作为切入点，精彩的木偶剧作为载体，使学生对本单元内容产生兴趣和求知欲"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。并试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (108, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看、听，并根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (109, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，句子之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (110, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型①What do you do on the weekend? ②When do you ...? ③I often/usually/sometimes ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第2页，大声朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (111, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 2个人一组\n2. 从课文的三组角色（第一组：Zhang Peng, Pedro；第二组：Amy, Sarah；第三组：Zoom,Zip）中各选1个角色\n3. 仿照课文图，进行角色对话\n4. 完成后，2人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组进行表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (112, '{"text": "在歌曲中呈现日常活动，使学生感受语言的工具性和艺术性"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。并试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (113, '{"text": "让学生对新句型在听觉上有所感知，并根据录音选出适当的图片"}', '{"text": "1. 看课本P4上-Let’s try板块。\n2. 阅读指示语，理解题目要求。\n3. 观察三幅图，说出各图的名称，并与同伴核对确认正确。\n4. 聆听录音，并根据录音内容选图，在所选图左下角划勾。\n5. 再听录音，核对答案。\n6. 与同伴核对讨论，然后确认答案。"}', '{"text": "同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视和监督学生认真完成听力活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (114, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看、听，并根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (115, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，句子之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (116, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型- When do you ...? - We ... at ... o’clock."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第4页，大声朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (117, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 2个人一组\n2. 1人扮演Zhang Peng，另1人扮演Pedro\n3. 仿照课文图，进行角色对话；\n4. 完成后，2人交换角色，再进行一轮对话"}', '{"text": "1. 同学们，我们来分组进行表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (119, '{"text": "通过角色扮演巩固Let’s talk中的核心句型When do you ...?以及在职业与特定作息时间之间建立联系"}', '{"text": "1. 观察图中灰条上的头饰都代表什么职业，与同伴讨论确认。说出各职业的英文名称。（这一步可以省略。）\n2. 阅读图中文字。再读Let’s talk对话，摘出其中以When开头的句子及回答。把这些问答都当成台词背下来。把课文当成“脚本”，备用。\n3. 两人一组。仿照图中问答，进行对话。（如果不省略第1步，就需戴上头饰，扮演角色，进行对话。）\n4. 交换问答角色，继续进行。问题问得越多越好。如果忘记了“台词”，可以看“脚本”。"}', '{"text": "1. 请把课本翻到第4页，看下面的活动\n2. 同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (120, '{"text": "运用核心句型- When do you ...? - I ... at ...开展问答活动，使学生将日常特定的活动与相应的时间联系在一起，以提高学生运用语言的能力，同时训练写的技能"}', '{"text": "1. 阅读指示语，理解题目要求。\n2. 阅读并大声读出下面的时间表上的各项日常活动。\n3. 大声读出图中对话框中小熊和松鼠的对话。\n4. 仿照小熊和松鼠的对话，两人一组进行对话练习，按照日程表询问对方每项日常活动时间。\n5. 然后交换角色，继续练习。"}', '{"text": "1. 请把课本翻到第5页，看下面的活动\n2. 同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (121, '{"text": "通过视听活动，复现本单元重点句型I usually/sometimes ..."}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频。并试着理解视频所表达的意思。"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (122, '{"text": "让学生对核心句型在听觉上有所感知，并根据录音选出适当的图片"}', '{"text": "1. 看课本P7上-Let’s try板块。\n2. 阅读指示语，理解题目要求。\n3. 观察三幅图，说出各图的名称，并与同伴核对确认正确。\n4. 聆听录音。根据录音内容选图，在所选图上划勾。\n5. 再听录音，核对答案。\n6. 与同伴核对讨论，然后确认答案。"}', '{"text": "同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视和监督学生认真完成听力活动\n3. 点击答案，让学生核对"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (123, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看、听，并根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (124, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，句子之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (125, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型- What do you do on the weekend? - I often/usually/sometimes ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第7页，大声朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (126, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 2个人一组\n2. 1人扮演shopkeeper，另1人扮演Sarah\n3. 仿照课文图，进行角色对话\n4. 完成后，2人交换角色，再进行一轮对话"}', '{"text": "1. 同学们，我们来分组进行表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动\n3. 表演结束后，我会请你们上台来展示"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (127, '{"text": "识记词汇clean my room, go for a walk, go shopping, take a dancing class并了解其在语境中的应用"}', '{"text": "1. 看图片理解词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习4个词组。根据图片猜一猜它们分别是什么意思！注意看词组、认真听录音，并小声跟读。"}', '{"text": "1. 依次呈现单词卡片，并且根据学生的反应，进行必要的重复呈现\n2. 引导学生借助图片理解词义，不能理解的词组，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (128, '{"text": "通过问答接龙活动操练核心句型- What do you do on the weekend? - I often/usually/sometimes ...和本单元核心词组"}', '{"text": "1. 阅读指示语，理解题目要求。\n2. 读出并尽量记住黄色板块里表述日常活动的短语，用在下面的活动里。\n3. 读出并尽量记住对话框里的问答语，作为下面活动的基本套语。\n4. 活动从第一个人开始。第一个人发问，第二个人回答；第二个人发问，第三个人回答，这样一个一个地一问一答进行下去，直至最后一个人。"}', '{"text": "1. 请把课本翻到第7页，看下面的活动\n2. 同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (129, '{"text": "训练学生词汇认读能力,巩固核心句型I always/often/sometimes ...，并锻炼学生写的能力"}', '{"text": "1. 阅读指示语，理解题目要求。用指示语提供的句子作为调查员的问语。\n2. 读出小熊对话框中的句子，作为被调查人回答询问的套语。\n3. 读表格里各板块的信息，用表格里各板块信息替换对话框句子的相应部分，组成若干新的句子，作为被调查人的回答。\n4. 小组里的同学轮流做调查员和被调查人。"}', '{"text": "1. 请把课本翻到第8页，看下面的活动\n2. 同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (130, '{"text": "Read, listen and chant:通过听例词发音、观察例词结构，学习cl和pl在单词中的发音规则，并通过说唱歌谣，强化记忆cl和pl的发音规则\nBingo:训练学生快速辨音的能力，强化cl和pl的音与形的对应关系\nChoose, write and say:在单线上完成抄写句子的活动，做到书写规范与正确"}', '{"text": "Read, listen and chant:\n1. 阅读指示语，理解题目要求。\n2. 聆听录音，跟读每个单词。然后独立大声读出每个单词，关注标红的字母的读音。\n3. 观看视频，学唱歌谣。\nBingo:\n（准备工作：1. 四人一组。每人做一张Bingo卡。上面画格子，横三竖三。横格长些，要能写下一个单词。如同课本图的Bingo卡。把本单元学过的单词都抄在上面。一格一词。为了游戏好玩，词的顺序不要与课本图的Bingo卡一致，互相之间也不要相同。2. 小组共同再做一套单词卡片。每个卡片上写一个本单元学过的单词。）\n1. 小组里推举一个人唱读单词。唱读者持有那一套单词卡，随意唱读单词卡上的单词。中间不要停顿，除非听到有人喊“Bingo！”。唱读速度可以与大家协商。（要记住自己读的是哪些单词，以便最后评判赢者时进行核对。）\n2. 其他三个人聚精会神地聆听，并在自己的Bingo卡上圈出所听到的那个单词。\n3. 当某同学Bingo卡上被圈出的单词首先连成线时，这位同学就喊“Bingo！”，成为赢家。（连成的线可以是横线，竖线，也可以是斜线。）\n4. 唱读单词的人听到“Bingo！”则停止唱读，和其他人共同对赢家进行核对确认。\n5. 更换唱读者，游戏继续。\nChoose, write and say:\n1. 看课本P6下-Choose, write and say活动。\n2. 大声朗读两个句子，并注意字母组合cl和pl的发音。\n3. 选择其中一句写在横线上，注意句子要书写规范。"}', '{"text": "Read, listen and chant：\n1. 请把课本翻到第6页，看上面的活动\n          2. 跟读8个单词，并体会字母组合cl和pl在单词中的发音规则\n3. 跟唱歌谣，要认真听认真唱哦\nBingo:\n1. 请把课本翻到第6页，看中间的活动\n2. 同活动步骤\nChoose, write and say:同活动步骤"}', '{"text": "Read, listen and chant:\n1. 带领学生理解题目要求（读一读，听一听，唱一唱）\n2. 引导学生听、读单词，并感知cl和pl在单词中的发音规则\n3. 播放歌谣，指导学生跟读跟唱\nBingo:\n1. 带领学生理解宾果游戏规则\n2. 指导并监督学生按照活动步骤完成游戏\nChoose, write and say:\n1. 引导学生大声、正确朗读\n2. 指导并监督学生认真、规范书写"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (131, '{"text": "能够在图片的帮助下读懂机器人的台词，并完成正误判断；能够模仿阅读篇章完成写信的任务"}', '{"text": "活动步骤一：\n1. 看课本P9-Read and write板块。\n2. 阅读短文故事，完成下面的理解题。\n3. 与同伴讨论答案。再看课文确认答案。\n活动步骤二：\n仿照故事里的句子，按照自己的实际情况，完成给Robinson的一封信。"}', '{"text": "同活动步骤"}', '{"text": "1. 引导学生合作学习，互帮互助，共同进步\n2. 点击答案，让学生核对"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (132, '{"text": "Listen and tick:通过听力活动，检测对本单元核心句型和词汇的理解与掌握情况\nGive answers about yourself:考查单元核心词汇的书写、意义理解以及在真实情景中的运用"}', '{"text": "Listen and tick：\n1. 看课本P10上-Let’s check板块中的Listen and tick.活动。\n2. 阅读指示语，理解题目要求。\n3. 观察四幅图，用英语说出各图里的人物在做什么，并与同伴核对确认正确。\n4. 听录音。根据录音内容选图，在所选图左下角划勾。\n5. 再听录音，核对答案。\n6. 与同伴核对，然后与大屏确认答案。\nGive answers about yourself:\n1. 看课本P10上-Let’s check板块中的Give answers about yourself.活动。\n2. 读指示语中的问题。\n3. 读3个回答的前半句，理解often, always, sometimes的意思。\n4. 根据实际情况回答每个问题。"}', '{"text": "同活动步骤"}', '{"text": " Listen and tick:\n1. 带领学生理解题目要求\n2. 播放录音，并监督学生认真完成练习\n3. 针对错误率较高的题目，重复播放音频供学生反复听练\n4. 点击答案，让学生核对答案。要引导学生之间互帮互助，以消化理解回答错误的题目\nGive answers about yourself:\n1. 带领学生理解题目要求\n2. 引导学生根据实际情况来完成句子"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (133, '{"text": "归纳总结go, play, have, do等动词与名词的搭配"}', '{"text": "1. 看课本P10下-Let’s wrap it up板块。\n2. 阅读指示语，理解题目要求。\n3. 阅读并理解表格上方的名词和名词短语的意思。\n4. 把表格上方的名词和短语中能与表格左栏的动词相搭配的，分别填写在右边相应的格里。如第一行的例子。\n5. 与同伴核对，然后与大屏确认答案。"}', '{"text": "同活动步骤"}', '{"text": " 1. 带领学生理解题目要求\n2. 监督学生认真完成练习\n3. 点击答案，让学生核对答案。要引导学生之间互帮互助，以消化理解回答错误的题目"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (134, '{"text": "能够在图片的帮助下理解故事内容，复习、巩固本单元的核心句型和词汇"}', '{"text": "1. 看课本P11-Story time板块，观察图。\n2. 听录音，看图。\n3. 听录音，同步默读相应文字。\n4. 阅读每幅图上的文字，结合图进一步理解意思。\n5. 与同伴谈论故事内容。试着讲故事。\n6. 看视频，完成拼图游戏。"}', '{"text": "1. 下面我们读一个有趣的小故事。Zip从周一到周末都很忙，Zip每天都做什么事呢？让我们开始阅读并寻找答案吧！\n2. 同活动步骤。"}', '{"text": " 1. 按照活动步骤带领学生阅读和欣赏故事\n2. 巡视并监督学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (135, '{"text": "在歌曲视频中呈现四季的英文名称及景象，让学生初步感知四季"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。并试着理解视频所表达的意思"}', '{"text": " 播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (136, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看、听，并根据视频猜测对话大意"}', '{"text": " 播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (137, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，句子之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (138, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型- Which season do you like best? - Autumn/Winter/... - Why? - Because I ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第12页，大声朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (139, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 2个人一组\n2. 从课文的三组角色（第一组：John, Amy；第二组：Zip, Zoom；第三组：Olive, Miss White）中各选1个角色\n3. 仿照课文图，进行角色对话\n4. 完成后，2人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组进行表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (140, '{"text": "通过歌曲呈现最喜欢的季节及原因，使学生进一步感受四季的美"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频，也可以跟唱。并试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (141, '{"text": "让学生感知新话题——季节与天气，并复习描述天气的词汇"}', '{"text": "1. 看课本P14上- Let’s try板块。\n2. 阅读指示语，理解活动要求。\n3. 大声读瓶子图上的文字。\n4. 聆听录音。根据听到的信息，在瓶子图上找到相应文字。\n5. 把它抄写到下面的横线上。\n6. 再听录音，核对答案。\n7. 与同伴核对讨论，然后确认答案。"}', '{"text": "同活动步骤"}', '{"text": "1. 带领学生明确活动步骤\n2. 巡视和监督学生认真完成听力活动\n3. 点击答案，让学生核对"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (142, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看、听，并根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (143, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，句子之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (144, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型- Which season do you like best? - Winter/Spring/..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第14页，大声朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (145, '{"text": "内化语言知识，提高听说技能."}', '{"text": "1. 3个人一组\n2. 1人扮演Mr Jones，1人扮演Mike，另1人扮演Wu Yifan\n3. 仿照课文图，进行角色对话；\n4. 完成后，3人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组进行表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (146, '{"text": "识记词汇spring, summer, autumn, winter，并了解其在语境中的应用"}', '{"text": "1. 看图片理解词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆；\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习4个单词。根据图片猜一猜它们的意思！注意看单词、认真听录音，并小声跟读。"}', '{"text": "1. 依次呈现单词卡片，并且根据学生的反应，进行必要的重复呈现\n2. 引导学生借助图片理解词义，不能理解的单词，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (147, '{"text": "运用句型Which season do you like best?询问他人喜好的季节"}', '{"text": "1. 看课本P14下-Which season do you like best? Ask and answer.活动。\n2. 读指示语，理解活动要求。\n3. 读对话框中的文字。\n4. 观察图，读每个图标上的单词，理解每幅图标代表什么季节。\n5. 在表格中Zoom栏，写上被调查者的名字。\n6. 模仿对话框内容，进行调查，在被调查者喜欢的季节下面划勾√。\n7. 在小组内，报告自己的调查结果。\n（下面以Zoom and Zip为例，推荐报告所用的句型：\n①Zoom likes winter.\n②Zoom and Zip like ... \n③Zoom, Zip and Mike like ...\n注意，报告某单个人喜欢某个季节时，动词要用likes，比如，Zoom likes winter.）"}', '{"text": "同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视并指导学生认真完成活动"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (148, '{"text": "操练和巩固Let’s learn板块有关季节的单词：spring, summer, autumn, winter"}', '{"text": "1. 看课本P15下-Read and match板块。\n2. 观察照片，确定每幅图的季节。\n3. 逐个读出每个句子。把每个句子与图片进行连线匹配。\n4. 与同伴核对讨论，然后点击答案进行确认。"}', '{"text": "同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视并指导学生认真完成活动\n3. 点击答案，让学生核对"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (149, '{"text": "在视频中呈现四季的特征，为学生陈述喜欢某个季节的理由提供参考"}', '{"text": "欣赏视频"}', '{"text": "同学们，现在请看大屏幕，欣赏视频。同时，试着理解视频所表达的意思"}', '{"text": "播放视频，监督学生认真观看，调动学生的学习热情和兴趣"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (150, '{"text": "让学生对核心句型在听觉上有所感知，并根据录音勾选出恰当的图片"}', '{"text": "1. 看课本P17上-Let’s try板块。\n2. 读指示语，理解题目要求。\n3. 观察图片，确定图片季节。\n4. 聆听录音。选出与录音内容相符的那张图，在图左上角心形框内画勾。\n5. 再听录音，核对答案。\n6. 与同伴核对讨论，然后与大屏确认答案"}', '{"text": "同活动步骤"}', '{"text": "1. 带领学生理解活动步骤\n2. 巡视和监督学生认真完成听力活动\n3. 点击答案，让学生核对"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (151, '{"text": "接触和初步理解课文"}', '{"text": "观看视频"}', '{"text": "同学们，现在请看大屏，认真看，认真听，根据视频猜测对话大意"}', '{"text": "播放视频，监督学生认真观看"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (152, '{"text": "理解课文大意、进一步熟悉课文，并跟读、模仿课文录音"}', '{"text": "逐句跟读"}', '{"text": "同学们，请认真听音频！我播放一句，大家就小声跟读一句"}', '{"text": "1. 逐句点击播放，句子之间给学生留出足够的跟读时间\n2. 引导学生借助图片理解句子，不能理解的句子，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (153, '{"text": "在理解课文的基础上帮助学生初步记忆课文，尤其是重点句型- Which season do you like best? - Summer/... - Why? - Because I ..."}', '{"text": "逐句朗读课文"}', '{"text": "同学们，请把课本翻到第17页，朗读课文"}', '{"text": "1. 巡视和监督学生朗读课文\n2. 学生独立朗读有困难的句子，需要重复点击播放录音供学生模仿\n3. 如果设备、环境和时间允许，可以请学生上讲台录音；得1朵小红花的句子，要进行重点训练"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (154, '{"text": "内化语言知识，提高听说技能"}', '{"text": "1. 2个人一组\n2. 1人扮演Miss White，1人扮演Amy\n3. 仿照课文图，进行角色对话\n4. 完成后，2人交换角色，再进行对话"}', '{"text": "1. 同学们，我们来分组表演\n2. 请认真看活动步骤，并且严格按照活动步骤来做表演活动\n3. 表演结束后，我会请你们上台来展示"}', '{"text": "1. 巡视并指导学生做角色对话练习\n2. 请学生做展示，时间允许的情况下，给尽可能多的学生展示机会"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (155, '{"text": "识记词组go on a picnic，go swimming，pick apples，make a snowman，并了解其在语境中的应用"}', '{"text": "1. 看图片猜测词义，查看中文释义确认词义，看词形识记拼写，听录音模仿并识记发音，最后将音形义整体记忆\n2. 看例句（及微语篇），了解词在语境中的应用"}', '{"text": "接下来，我们学习4个词组。根据图片猜一猜它们的意思！注意看词组、认真听录音，并小声跟读。"}', '{"text": "1. 依次呈现词组卡片，并且根据学生的反应，进行必要的重复呈现\n2. 引导学生借助图片理解词义，不能理解的词组，可以查看中文释义"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (156, '{"text": "运用句型- Which season do you like best? - I like ... best. - Why? - Because ...做关于喜好的季节及其原因的问答"}', '{"text": "1. 大声读对话框中的内容，理解对话的意思。\n2. 复读Let’s talk的内容，获取关于季节的信息。\n3. 套用对话框句式，利用Let’s talk的信息，两人一组进行问答。"}', '{"text": "1. 请把课本翻到第17页，看下面的活动\n2. 同活动步骤"}', '{"text": "1. 带领学生理解活动要求\n2. 巡视并指导学生认真完成活动\n3. 如果时间允许，可以请学生到讲台上展示"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (157, '{"text": "巩固A和B部分Let’s talk及Let’s learn板块的核心句型和词组"}', '{"text": "1. 大声读对话框中的内容，理解对话的意思。\n2. 大声读出右面紫色椭圆色块中的短语。理解每项活动适合什么季节。\n3. 套用对话框句式，从紫色椭圆色块中选择信息，进行问答活动。\n4. 两人轮流问答。"}', '{"text": "1. 请把课本翻到第18页，看下面的活动\n2. 同活动步骤"}', '{"text": "1.带领学生理解活动要求\n2. 巡视并指导学生认真完成活动\n3. 如果时间允许，可以请学生到讲台上展示"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (158, '{"text": "Read, listen and chant:能够掌握字母组合br和gr的发音规则，即br和gr在单词中分别发/br/和/gr/\nRead and group:能够读出符合br和gr发音规则的单词，并根据br和gr的发音规则拼写出对应的单词\nChoose, write and say:能够在单线上完成句子抄写，做到书写规范正确"}', '{"text": "Read, listen and chant:\n1. 聆听录音。\n2. 看着单词，跟读。注意标红色的组合字母。\n3. 听录音，直至看见拼写即能正确读出声音。\n4. 观看视频，学唱歌谣。\nRead and group:\n1. 看课本P16中Read and group.活动。\n2. 大声读浅蓝色色块里的每个单词。\n3. 观察、朗读，并记住圆形橘红色块的两个字母组合和浅蓝色块里的示范词的读音。\n4. 把浅蓝色块里的单词，按照字母组合及示范词的读音，分成两组，分别抄写到相应板块里示范词的后面。\n5. 分别朗读两组单词，进一步检查调整。\nChoose, write and say:\n1. 看课本P16下Choose, write and say.活动。\n2. 大声读淡绿色色块里的每个单词。\n3. 大声读图片下面的句子。\n4. 选择合适的单词连成一个或者多个句子，并大声读出句子。\n5. 把一个句子抄写在2后面的横线上，注意书写规范。\n6. 与同伴互相说说自己的句子。"}', '{"text": "Read, listen and chant:\n1. 请把课本翻到第16页，看上面的活动\n2. 跟读8个单词，并体会字母组合br和gr在单词中的发音规则\n3. 跟唱歌谣，要认真听认真唱哦\nRead and group:同活动步骤\nChoose, write and say:同活动步骤"}', '{"text": "Read, listen and chant:\n1. 带领学生理解题目要求（读一读，听一听，唱一唱）\n2. 引导学生听、读单词，并感知br和gr在单词中的发音规则\n3. 播放歌谣，指导学生跟读跟唱\nRead and group:\n1. 引导学生根据发音规则读出单词\n2. 引导学生按字母组合及其发音给单词正确分类\nChoose, write and say:\n1. 引导学生大声、正确朗读\n2. 指导并监督学生认真、规范书写"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (159, '{"text": "能够在图片的帮助下读懂对四季的描述以及机器人对四季喜好的表达，并完成选择题和句子填写活动"}', '{"text": "活动步骤一：\n1. 看课本P19-Read and write板块。\n2. 看图，读图上方的说明和图内对话框里的内容，理解意思。\n3. （Read and tick）读图下面的题，理解意思，根据图文所表达的内容，从选项中选择正确的一个，在所选项前面方框里画勾。\n4. 与同伴讨论答案。再看课文确认答案。然后与大屏确认答案。\n活动步骤二：（Complete the sentence）\n读指示语，理解题目要求。把答案写在空白线上完成句子。"}', '{"text": "同活动步骤"}', '{"text": "1. 引导学生借助图片来理解句子的意思\n2. 引导学生合作学习，互帮互助，共同进步\n3. 点击答案，让学生核对"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (160, '{"text": "Listen and write：通过听力活动，检测评价学生对本单元核心句型的理解与掌握"}', '{"text": "Listen and write：\n1. 看课本P20上-Let’s check板块。\n2. 浏览带有空白的对话，理解大意。\n3. 聆听录音。补全对话。\n4. 再听录音，核对答案。\n5. 与同伴核对，然后与大屏确认答案。"}', '{"text": "同活动步骤"}', '{"text": "Listen and write：\n1. 带领学生理解题目要求\n2. 播放录音，并监督学生认真完成练习\n3. 针对错误率较高的题目，重复播放音频供学生反复听练\n4. 点击答案，让学生核对答案。要引导学生之间互帮互助，以消化理解回答错误的题目"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (161, '{"text": "巩固和复习特殊疑问词what、when、where、which、why的用法"}', '{"text": "1. 看课本P20下-Let’s wrap it up板块。\n2. 读右面紫色色块里的疑问词。\n3. 读带有空白的句子，理解句子意思。推理，猜一猜空白处是什么意思。\n4. 从紫色色块中选择恰当的疑问词填入句子空白处。\n5. 复读每个句子，检查所填的疑问词是否正确。\n6. 与同伴核对，然后与大屏确认答案。"}', '{"text": "同活动步骤"}', '{"text": "1. 带领学生理解题目要求（即用疑问词填空）\n2. 引导学生通过观察答句来选择问句的疑问词\n3. 监督学生认真完成练习\n4. 点击答案，让学生核对答案。要引导学生之间互帮互助，以消化理解回答错误的题目"}', NULL);
INSERT INTO "textbooks_prepareadvise"("id", "purpose", "step", "statement", "task", "video_id") VALUES (162, '{"text": "能够在图片的帮助下理解故事内容，复习、巩固有关四季的句型和词汇"}', '{"text": "1. 看课本P21-Story time板块，观察图。\n2. 听录音，看图。\n3. 听录音，同步默读相应文字。\n4. 阅读每幅图上的文字，结合图进一步理解意思。\n5. 与同伴谈论故事内容。试着讲故事。\n6. 看视频，完成拼图游戏。"}', '{"text": "1. 下面我们读一个有趣的小故事。澳大利亚的Koala来中国过圣诞节，看见雪他感到十分惊喜，到底中国与澳大利过亚圣诞节时有什么不同呢？让我们从故事中寻找答案吧。\n2. 同活动步骤。"}', '{"text": "1. 按照活动步骤带领学生阅读和欣赏故事\n2. 提示中国和澳大利亚季节的差异"}', NULL);
