INSERT INTO "textbooks_activity"("id", "title", "en_title", "subtitle", "en_subtitle", "short_name", "order", "is_active", "description", "create_time", "update_time", "display_type", "integral", "suggested_time", "rule", "knowledge_point", "en_des", "act_tab_id", "act_type_id", "image_id", "lesson_id", "prepare_advise_id", "publish_id", "textbook_id", "unit_id", "rule_data") VALUES (143, 'Make a monster', '游戏', '', '', '', 6, 't', 'PEP3B Unit 3 Lesson3', '2019-01-23 17:20:22.8765+08', '2019-02-27 09:14:43.217352+08', 7, 0, 180000, '{"type": 8, "process": [{"left": {"title": "步骤一", "image_id": 279}, "order": 1, "right": {"text": "熟读身体部位词：head, ears, eyes, nose, arms, body, legs等。"}, "status": 2}, {"left": {"title": "步骤二", "image_id": 279}, "order": 2, "right": {"text": "熟读对话框里的句子。"}, "status": 2}, {"left": {"title": "步骤三", "image_id": 279}, "order": 3, "right": {"text": "2~4人一组，小组成员分别在纸上画任意动物身体的任意部位，\n如：head, ears, eyes, nose, arms, body, leg, foot等，\n边画边说出这个部位的名称。注意，所画的每个部位的横宽要一致，\n上下要充满每张纸，这样，每个部分拼接在一起才能无缝衔接\n，形成一幅有趣的图。"}, "status": 2}, {"order": 4, "middle": {"topic": "Monster"}, "status": 1, "is_show": true}, {"left": {"title": "步骤四", "show_works": true}, "order": 5, "right": {"text": "个人仿照对话框句型发令，“It has ….”，其他人赶快找到相应部位，\n仿照对话框句型回应，“Ok. Here’s + 单数/Here’re + 复数 ….”\n并把这个部位图摆放到桌面上相应位置，大家一起说出这个部位的名称。"}, "status": 2}, {"left": {"title": "步骤五", "show_works": true}, "order": 6, "right": {"text": "随着发令人的要求，一个部位接一个部位地拼接起来，\n拼完最后一张时，大家一起说Monster！各组可以互相参观拼出来的Monster。"}, "status": 2}, {"left": {"title": "步骤六", "show_works": true}, "order": 7, "right": {"text": "换人发令，继续玩。\n提示：在游戏之前，建议先熟读下列提示，以便游戏能顺畅进行。\n- It has a (big/small) head/a( big/long) nose/a (big/long) body/….\n- Here’s a head/a nose/a body/….\n- It has (long/short) arms/legs/ears/(big/small) eyes/….\n- Here’re the arms/the eyes/the ears/the legs/…."}, "status": 2}]}', '{}', '', 4, 3, 570, 16, NULL, 2, 10, 66, '{"type": 8, "process": [{"left": {"title": "步骤一", "image_id": {"id": 279, "res": "/media/res/confres/course/image/contentpic/learn/3b3/l3_make.jpg", "title": "_make"}}, "order": 1, "right": {"text": "熟读身体部位词：head, ears, eyes, nose, arms, body, legs等。"}, "status": 2}, {"left": {"title": "步骤二", "image_id": {"id": 279, "res": "/media/res/confres/course/image/contentpic/learn/3b3/l3_make.jpg", "title": "_make"}}, "order": 2, "right": {"text": "熟读对话框里的句子。"}, "status": 2}, {"left": {"title": "步骤三", "image_id": {"id": 279, "res": "/media/res/confres/course/image/contentpic/learn/3b3/l3_make.jpg", "title": "_make"}}, "order": 3, "right": {"text": "2~4人一组，小组成员分别在纸上画任意动物身体的任意部位，\n如：head, ears, eyes, nose, arms, body, leg, foot等，\n边画边说出这个部位的名称。注意，所画的每个部位的横宽要一致，\n上下要充满每张纸，这样，每个部分拼接在一起才能无缝衔接\n，形成一幅有趣的图。"}, "status": 2}, {"order": 4, "middle": {"topic": "Monster"}, "status": 1, "is_show": true}, {"left": {"title": "步骤四", "show_works": true}, "order": 5, "right": {"text": "个人仿照对话框句型发令，“It has ….”，其他人赶快找到相应部位，\n仿照对话框句型回应，“Ok. Here’s + 单数/Here’re + 复数 ….”\n并把这个部位图摆放到桌面上相应位置，大家一起说出这个部位的名称。"}, "status": 2}, {"left": {"title": "步骤五", "show_works": true}, "order": 6, "right": {"text": "随着发令人的要求，一个部位接一个部位地拼接起来，\n拼完最后一张时，大家一起说Monster！各组可以互相参观拼出来的Monster。"}, "status": 2}, {"left": {"title": "步骤六", "show_works": true}, "order": 7, "right": {"text": "换人发令，继续玩。\n提示：在游戏之前，建议先熟读下列提示，以便游戏能顺畅进行。\n- It has a (big/small) head/a( big/long) nose/a (big/long) body/….\n- Here’s a head/a nose/a body/….\n- It has (long/short) arms/legs/ears/(big/small) eyes/….\n- Here’re the arms/the eyes/the ears/the legs/…."}, "status": 2}]}');
