INSERT INTO "textbooks_activity"("id", "title", "en_title", "subtitle", "en_subtitle", "short_name", "order", "is_active", "description", "create_time", "update_time", "display_type", "integral", "suggested_time", "rule", "knowledge_point", "en_des", "act_tab_id", "act_type_id", "image_id", "lesson_id", "prepare_advise_id", "publish_id", "textbook_id", "unit_id", "rule_data") VALUES (538, 'Let''s spell', '拼读练习', '', '', '', 1, 't', 'PEP5B Unit 1 Lesson4', '2019-01-23 17:20:22.9215+08', '2019-02-27 09:14:46.187347+08', 7, 0, 300000, '{"type": 7, "process": [{"left": {"title": "活动1 Read, listen and chant.", "image_id": 898}, "order": 1, "right": {"text": "阅读指示语，理解题目要求。"}, "status": 2}, {"left": {"audio_id": 1113, "image_id": 898}, "order": 2, "right": {"text": "聆听录音，跟读每个单词。然后独立大声读出每个单词，关注标红的字母的读音。"}, "status": 2}, {"left": {"title": "活动2 Bingo（宾果）", "image_id": 899}, "order": 3, "right": {"text": "准备：1.每人做一张Bingo卡。上面画格子，横三竖三。横格长些，要能写下一个单词。如同课本图的Bingo卡。把本单元学过的单词都抄在上面。一格一词。为了游戏好玩，词的顺序不要与课本图的Bingo卡一致，互相之间也不要相同。\n2. 小组共同再做一套单词卡片。每个卡片上写一个本单元学过的单词。"}, "status": 2}, {"left": {"image_id": 899}, "order": 4, "right": {"text": " 小组里推举一个人唱读单词。唱读者持有那一套单词卡，随意唱读单词卡上的单词。中间不要停顿，除非听到有人喊“Bingo！”。唱读速度可以与大家协商。（要记住自己读的是哪些单词，以便最后评判赢者时进行核对。）"}, "status": 2}, {"left": {"image_id": 899}, "order": 5, "right": {"text": "其他三个人聚精会神地聆听，并在自己的Bingo卡上圈出所听到的那个单词。"}, "status": 2}, {"left": {"image_id": 899}, "order": 6, "right": {"text": "当某同学Bingo卡上被圈出的单词首先连成线时，这位同学就喊“Bingo！”，成为赢家。（连成的线可以是横线，竖线，也可以是斜线。）"}, "status": 2}, {"left": {"image_id": 899}, "order": 7, "right": {"text": "唱读单词的人听到“Bingo！”则停止唱读，和其他人共同对赢家进行核对确认。"}, "status": 2}, {"left": {"image_id": 899}, "order": 8, "right": {"text": "更换唱读者，游戏继续。", "button": {"name": "查看答案", "answer": {"image_id": 900}}}, "status": 2}, {"left": {"title": "活动3  Choose, write and say", "image_id": 901}, "order": 9, "right": {"text": "看课本P6下- Choose, write and say活动。"}, "status": 2}, {"left": {"image_id": 901}, "order": 10, "right": {"text": "大声朗读两个句子，并注意字母组合cl和pl的发