INSERT INTO "public"."questions_question" VALUES (3564, '01', '04', 'b', 'jcfx', 'zhfx', '', '2', '类型一', 10, '', '根据上下文，从所给选项中进行选择填入空白处，使句子意思完整，语法正确。每个选项只能用一次。有一个多余选项。', '1. - || is this skirt?\n    - It''s $89.\n2. - || horses do you have?\n    - Seventeen.\n3. - || like this skirt?\n    - It''s very pretty.', '', 'How do you|How are you|How many|How much', 'How much|How many|How do you', '综合复习（一）', '', '', 'f');
INSERT INTO "public"."questions_question" VALUES (3565, '01', '04', 'b', 'jcfx', 'zhfx', '', '3', '类型一', 10, '', '从所给选项中选择恰当的应答语。每个选项只能用一次。', 'What time is it?|It’s nine o’clock.&What are these?|They’re carrots.&Whose pants are those?|They’re your brother’s.&Where is the teachers’ office? |It’s on the first floor.&What’s the weather like in Beijing?|It’s hot.', '', '', '', '综合复习（一）', '', '', 'f');
INSERT INTO "public"."questions_question" VALUES (3566, '01', '04', 'b', 'jcfx', 'zhfx', '', '2', '类型一', 10, '', '从所给选项中选择恰当的应答语。每个选项只能用一次。', '1. - How many T-shirts do you have?\n    - ||\n2. - How much is this T-shirt?\n    - ||\n3. - How do you like this T-shirt?\n    - ||', '', 'Six.|It''s $6.|It’s very nice.', 'Six.|It''s $6.|It’s very nice.', '综合复习（一）', '', '', 'f');
INSERT INTO "public"."questions_question" VALUES (3567, '01', '04', 'b', 'jcfx', 'zhfx', '', '2', '类型一', 10, '', '从所给选项中选择恰当的应答语。每个选项只能用一次。', '1. - Can I help you?\n    - ||\n2. - Can I have some juice?\n    - || Have some water, please.\n3. - I like this T-shirt. Can I try it on?\n    - ||', '', 'Yes. Some orange juice, please.|No, you can’t.|Sure. Here you are.', 'Yes. Some orange juice, please.|No, you can’t.|Sure. Here you are.', '综合复习（一）', '', '', 'f');
INSERT INTO "public"."questions_question" VALUES (3568, '01', '04', 'b', 'jcfx', 'zhfx', '', '2', '类型一', 10, '', '读下面对话，猜测说话人的身份和关系，进行推理选择。', '1. - ||\n    - Yes. How much is this skirt?\n2. - I like the pants.||\n    - Of course. Here you are.\n3. - ||\n    - No, you can''t.', '', 'Can I help you?|Can I try them on?|Can I have some sweets?', 'Can I help you?|Can I try them on?|Can I have some sweets?', '综合复习（一）', '', '', 'f');
