INSERT INTO "public"."questions_question" VALUES (2177, '01', '05', 'b', '04', '4', '', '2', '类型一', 10, '', '选出下列每组单词中不同类的一项。', '1.(||)\nA. one     B. two     C. first\n2.(||)\nA. Monday     B. October     \nC. December\n3.(||)\nA. second     B. third     C. four\n4.(||)\nA. April Fool’s Day     B. January     C. February\n5.(||)\nA. Children’s Day     B. New Year’s Day     C. spring', '', 'A|B|C', 'C|A|C|A|C', '知识练习', '', '', 'f');
