INSERT INTO "public"."questions_question" VALUES (2217, '01', '04', 'b', '02', 'dyjc', '', '1', '类型一', 10, '', '根据所给的情景，选择合适的句子填空。', '妈妈催促你去吃饭，你要等一下才能去，可以说：________', '', 'Just a minute.|Come on.', 'Just a minute.', '单元检测', '目的：关注和熟悉本单元所学句型和短语', '', 'f');
