INSERT INTO "public"."questions_question" VALUES (2220, '01', '04', 'b', '02', 'dyjc', '', '2', '类型一', 10, '', '阅读短文，判断正（T）误（F）。', 'Hi, my name is Lisa. I’m nine years old. I’m a student. \nIt’s 6:20 a.m. It’s time to get up. \nIt’s 7 o’clock. It’s time to go to school. \nI have four classes in the morning and two classes in the afternoon. \nIt’s 10 o’clock. It’s time for English class.\nThis afternoon, I have a PE class. I can jump and run.\nThis afternoon, I have an art class, too. The art room is big. It’s on the second floor. \nOh! It’s 5 o’clock now. It’s time to go home. Goodbye!\n\n（||）1. Lisa is a student.\n（||）2. It’s 7 o’clock. It’s\ntime to get up.\n（||）3. This morning, Lisa\nhas an art class.\n（||）4. The art room is\non the second floor.\n（||）5. It’s 4 o’clock. It’s\ntime to go home.', '', 'T|F', 'T|F|F|T|F', '单元检测', '目的：训练阅读理解能力', '', 'f');
