INSERT INTO "public"."questions_question" VALUES (2539, '01', '05', 'b', '02', 'dyjc', '', '2', '类型一', 10, '', '阅读短文，判断对（√）错（╳）。', 'I am John. There’re four people in my family. My father, my mother, my brother and I. My father is a doctor. He likes fall best because he can draw many beautiful pictures. My mother is a nurse. She likes winter. She likes snow. My brother likes summer best, because he can eat ice cream. I like summer, too. Because I can go swimming in summer.\n\n(||)1. John has a sister.\n(||)2. John’s father can draw many beautiful pictures in autumn.\n(||)3. John’s mother is a doctor.\n(||)4. John likes summer, because he can eat ice cream.\n(||)5. John’s father and John likes summer.', '', '√|╳', '╳|√|╳|╳|╳', '单元检测', '目的：训练阅读理解能力', '', 'f');
